import React, { useContext, useState, useRef, Fragment } from "react";
import { HOST_API } from "../Componentes/reducer";
import { Store } from "../Componentes/Store";
import "./Styles.css";


export const FormTodo = () => {
  const formRef = useRef(null);
  const {
    dispatch, state: { item },
  } = useContext(Store);
  const [state, setState] = useState({ item });

  const onAdd = (event) => {
    const request = {
      name: state.name,
      id: null,
      isCompleted: false,
    };

    fetch(HOST_API + "/todo", {
      method: "POST",
      body: JSON.stringify(request),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => response.json())
      .then((todo) => {
        dispatch({ type: "add-item", item: todo });
        setState({ name: "" });
        formRef.current.reset();
      });
  };

  const onEdit = (event) => {
    const request = {
      name: state.name,
      id: item.id,
      isCompleted: item.isCompleted,
    };

    fetch(HOST_API + "/todo", {
      method: "PUT",
      body: JSON.stringify(request),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => response.json())
      .then((todo) => {
        dispatch({ type: "update-item", item: todo });
        setState({ name: "" });
        formRef.current.reset();
      });
  };

  return (
    <form ref={formRef} className="InArriba">
      <input
        className="Input"
        placeholder="Agrega una Tarea"
        type="text"
        name="name"
        defaultValue={item.name}
        onChange={(event) => {
          setState({ ...state, name: event.target.value });
        }}
      ></input>
      {item.id && (
        <button id="Actualizar" class="btn btn-primary" onClick={onEdit}>
          Actualizar
        </button>
      )}
      {!item.id && (
        <button id="Agregar" class="btn btn-primary" onClick={onAdd}>
          Agregar
        </button>
      )}
    </form>
  );
};

export default FormTodo;
